import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../services/api';
import './Booking.css';

const Booking = () => {
  const [bookingDetails, setBookingDetails] = useState({
    date: '',
    time: '',
    phoneNumber: '',
    numberOfTables: 1,
    selectedGames: [],
    eventType: '',
    guestCount: '',
    specialRequests: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { user } = useAuth();

  const gameOptions = [
    { id: 'poker', name: 'Poker' },
    { id: 'blackjack', name: 'Blackjack' },
    { id: 'roulette', name: 'Roulette' },
    { id: 'craps', name: 'Craps' }
  ];

  const eventTypes = [
    'Corporate Event',
    'Birthday Party',
    'Wedding',
    'Fundraiser',
    'Holiday Party',
    'Other'
  ];

  const timeSlots = [
    '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM',
    '5:00 PM', '6:00 PM', '7:00 PM', '8:00 PM', '9:00 PM',
    '10:00 PM', '11:00 PM'
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingDetails(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGameSelection = (e) => {
    const { checked, value } = e.target;
    setBookingDetails(prev => ({
      ...prev,
      selectedGames: checked
        ? [...prev.selectedGames, value]
        : prev.selectedGames.filter(game => game !== value)
    }));
  };

  // client/src/components/pages/Booking.js
const handleSubmit = async (e) => {
  e.preventDefault();

  if (!user) {
    setError('Please log in or sign up to complete your booking');
    return;
  }

  setError('');
  setSuccess('');
  setLoading(true);

  try {
    if (!bookingDetails.date || !bookingDetails.time || !bookingDetails.eventType || !bookingDetails.phoneNumber) {
      throw new Error('Please fill in all required fields');
    }

    if (bookingDetails.selectedGames.length === 0) {
      throw new Error('Please select at least one game');
    }

    // Validate phone number format
    const phoneRegex = /^\d{3}-\d{3}-\d{4}$/;
    if (!phoneRegex.test(bookingDetails.phoneNumber)) {
      throw new Error('Please enter a valid phone number in the format 123-456-7890');
    }

    const response = await api.post('/bookings', {
      ...bookingDetails,
      userId: user.id
    });

    setSuccess('Booking successful! You will receive a confirmation email shortly.');
    window.location.href = '/dashboard';
  } catch (err) {
    console.error('Booking error:', err);
    setError(err.response?.data?.message || err.message || 'Failed to create booking');
  } finally {
    setLoading(false);
  }
};

  const today = new Date().toISOString().split('T')[0];
  const maxDate = new Date();
  maxDate.setMonth(maxDate.getMonth() + 6);
  const maxDateString = maxDate.toISOString().split('T')[0];

  return (
    <div className="booking-container">
      <h1>Book Your Casino Event</h1>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <form onSubmit={handleSubmit} className="booking-form">
        <div className="form-group">
          <label>Event Date:</label>
          <input
            type="date"
            name="date"
            value={bookingDetails.date}
            onChange={handleInputChange}
            min={today}
            max={maxDateString}
            required
          />
        </div>

        <div className="form-group">
          <label>Phone Number:</label>
          <input
            type="tel"
            name="phoneNumber"
            value={bookingDetails.phoneNumber}
            onChange={handleInputChange}
            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
            placeholder="123-456-7890"
            required
          />
          <small>Format: 123-456-7890</small>
        </div>

        <div className="form-group">
          <label>Event Time:</label>
          <select
            name="time"
            value={bookingDetails.time}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a time</option>
            {timeSlots.map(time => (
              <option key={time} value={time}>{time}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Event Type:</label>
          <select
            name="eventType"
            value={bookingDetails.eventType}
            onChange={handleInputChange}
            required
          >
            <option value="">Select event type</option>
            {eventTypes.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Number of Tables:</label>
          <input
            type="number"
            name="numberOfTables"
            value={bookingDetails.numberOfTables}
            onChange={handleInputChange}
            min="1"
            max="10"
            required
          />
        </div>

        <div className="form-group">
          <label>Expected Guest Count:</label>
          <input
            type="number"
            name="guestCount"
            value={bookingDetails.guestCount}
            onChange={handleInputChange}
            min="1"
            required
          />
        </div>

        <div className="form-group">
          <label>Select Games:</label>
          <div className="games-selection">
            {gameOptions.map(game => (
              <div key={game.id} className="game-option">
                <input
                  type="checkbox"
                  id={game.id}
                  value={game.name}
                  checked={bookingDetails.selectedGames.includes(game.name)}
                  onChange={handleGameSelection}
                />
                <label htmlFor={game.id}>{game.name}</label>
              </div>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label>Special Requests:</label>
          <textarea
            name="specialRequests"
            value={bookingDetails.specialRequests}
            onChange={handleInputChange}
            rows="4"
            placeholder="Any special requirements or requests for your event?"
          />
        </div>

        {user ? (
          <button 
            type="submit" 
            className={`submit-button ${loading ? 'loading' : ''}`}
            disabled={loading}
          >
            {loading ? 'Creating Booking...' : 'Book Event'}
          </button>
        ) : (
          <div className="auth-prompt">
            <p>Please log in or sign up to complete your booking</p>
            <div className="auth-buttons">
              <Link to="/login" className="auth-button login">
                Login
              </Link>
              <Link to="/register" className="auth-button register">
                Sign Up
              </Link>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default Booking;
