// client/src/components/admin/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import { format, isPast } from 'date-fns';
import api from '../../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [bookings, setBookings] = useState([]);
  const [activeTab, setActiveTab] = useState('upcoming');
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      const response = await api.get('/admin/bookings');
      setBookings(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch bookings');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteBooking = async (bookingId) => {
    if (window.confirm('Are you sure you want to delete this booking? This action cannot be undone.')) {
      try {
        await api.delete(`/admin/bookings/${bookingId}`);
        fetchBookings(); // Refresh the list
        alert('Booking deleted successfully');
      } catch (err) {
        alert('Failed to delete booking');
        console.error(err);
      }
    }
  };

  const filterBookings = () => {
    return bookings.filter(booking => {
      const isUpcoming = !isPast(new Date(booking.date));
      const matchesTab = 
        (activeTab === 'upcoming' && isUpcoming) ||
        (activeTab === 'past' && !isUpcoming);

      const matchesSearch = 
        booking.userId?.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        booking.userId?.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        booking.eventType?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        booking.phoneNumber?.includes(searchTerm);

      const matchesStatus = filterStatus === 'all' || booking.status === filterStatus;

      return matchesTab && matchesSearch && matchesStatus;
    });
  };

  const formatDate = (date) => format(new Date(date), 'PPP');

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>Admin Dashboard</h1>
      </div>

      <div className="admin-tabs">
        <button 
          className={`tab ${activeTab === 'upcoming' ? 'active' : ''}`}
          onClick={() => setActiveTab('upcoming')}
        >
          Upcoming Events
        </button>
        <button 
          className={`tab ${activeTab === 'past' ? 'active' : ''}`}
          onClick={() => setActiveTab('past')}
        >
          Past Events
        </button>
      </div>

      <div className="controls">
        <input
          type="text"
          placeholder="Search bookings..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="status-filter"
        >
          <option value="all">All Status</option>
          <option value="pending">Pending</option>
          <option value="confirmed">Confirmed</option>
          <option value="cancelled">Cancelled</option>
        </select>
      </div>

      <div className="bookings-table">
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Customer</th>
              <th>Event Type</th>
              <th>Status</th>
              <th>Games</th>
              <th>Guests</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filterBookings().map((booking) => (
              <tr key={booking._id}>
                <td>{formatDate(booking.date)}</td>
                <td>
                  {booking.userId ? 
                    `${booking.userId.firstName} ${booking.userId.lastName}` :
                    'Unknown User'
                  }
                </td>
                <td>{booking.eventType}</td>
                <td>
                  <select
                    value={booking.status}
                    onChange={async (e) => {
                      try {
                        await api.patch(`/admin/bookings/${booking._id}/status`, {
                          status: e.target.value
                        });
                        fetchBookings();
                      } catch (err) {
                        alert('Failed to update status');
                      }
                    }}
                    className={`status-select ${booking.status}`}
                  >
                    <option value="pending">Pending</option>
                    <option value="confirmed">Confirmed</option>
                    <option value="cancelled">Cancelled</option>
                  </select>
                </td>
                <td>{booking.selectedGames.join(', ')}</td>
                <td>{booking.guestCount}</td>
                <td>
                  <button
                    onClick={() => handleDeleteBooking(booking._id)}
                    className="delete-btn"
                    title="Delete Booking"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
