import React, { useState, useEffect } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameMonth, 
         isToday, addMonths, subMonths, parseISO, isSameDay } from 'date-fns';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../services/api';

const InteractiveBookingCalendar = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedTime, setSelectedTime] = useState('');
  const [bookingDetails, setBookingDetails] = useState({
    date: '',
    time: '',
    phoneNumber: '',
    numberOfTables: 1,
    selectedGames: [],
    eventType: '',
    guestCount: '',
    specialRequests: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [dateAvailability, setDateAvailability] = useState({});
  const { user } = useAuth();

  const timeSlots = [
    '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM',
    '5:00 PM', '6:00 PM', '7:00 PM', '8:00 PM', '9:00 PM',
    '10:00 PM', '11:00 PM'
  ];

  const gameOptions = [
    { id: 'poker', name: 'Poker' },
    { id: 'blackjack', name: 'Blackjack' },
    { id: 'roulette', name: 'Roulette' },
    { id: 'craps', name: 'Craps' }
  ];

  const eventTypes = [
    'Corporate Event',
    'Birthday Party',
    'Wedding',
    'Fundraiser',
    'Holiday Party',
    'Other'
  ];

  useEffect(() => {
    checkMonthAvailability(currentMonth);
  }, [currentMonth]);

  const checkMonthAvailability = async (month) => {
    try {
      const start = startOfMonth(month);
      const end = endOfMonth(month);
      const days = eachDayOfInterval({ start, end });

      const availabilityPromises = days.map(day => 
        api.get(`/bookings/availability/${format(day, 'yyyy-MM-dd')}`)
      );

      const results = await Promise.all(availabilityPromises);
      const availability = {};

      days.forEach((day, index) => {
        availability[format(day, 'yyyy-MM-dd')] = results[index].data.available;
      });

      setDateAvailability(availability);
    } catch (err) {
      console.error('Error checking availability:', err);
    }
  };

  const handleDateSelect = async (date) => {
    setSelectedDate(date);
    setBookingDetails(prev => ({
      ...prev,
      date: format(date, 'yyyy-MM-dd')
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      setError('Please log in or sign up to complete your booking');
      return;
    }

    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await api.post('/bookings', {
        ...bookingDetails,
        userId: user.id
      });

      if (response.data.success) {
        setSuccess('Booking request submitted! We will contact you shortly to confirm your event.');
        setBookingDetails({
          date: '',
          time: '',
          phoneNumber: '',
          numberOfTables: 1,
          selectedGames: [],
          eventType: '',
          guestCount: '',
          specialRequests: ''
        });
        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 3000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create booking');
    } finally {
      setLoading(false);
    }
  };

  const renderCalendarHeader = () => (
    <div className="flex items-center justify-between mb-4">
      <button
        onClick={() => setCurrentMonth(subMonths(currentMonth, 1))}
        className="p-2 hover:bg-gray-100 rounded"
      >
        Previous
      </button>
      <h2 className="text-xl font-bold">
        {format(currentMonth, 'MMMM yyyy')}
      </h2>
      <button
        onClick={() => setCurrentMonth(addMonths(currentMonth, 1))}
        className="p-2 hover:bg-gray-100 rounded"
      >
        Next
      </button>
    </div>
  );

  const renderCalendarDays = () => {
    const days = eachDayOfInterval({
      start: startOfMonth(currentMonth),
      end: endOfMonth(currentMonth)
    });

    return (
      <div className="grid grid-cols-7 gap-2">
        {days.map((day) => {
          const dateString = format(day, 'yyyy-MM-dd');
          const isAvailable = dateAvailability[dateString];
          const isSelected = isSameDay(day, selectedDate);

          return (
            <button
              key={day.toString()}
              onClick={() => handleDateSelect(day)}
              disabled={!isAvailable}
              className={`
                p-4 relative rounded-lg
                ${isSelected ? 'bg-blue-500 text-white' : ''}
                ${!isAvailable ? 'bg-gray-200 cursor-not-allowed' : 'hover:bg-gray-100'}
                ${isToday(day) ? 'border-2 border-blue-500' : ''}
              `}
            >
              <span>{format(day, 'd')}</span>
              {isAvailable && (
                <span className="absolute bottom-1 right-1 w-2 h-2 bg-green-500 rounded-full"></span>
              )}
            </button>
          );
        })}
      </div>
    );
  };

  return (
    <div className="max-w-7xl mx-auto p-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Calendar Section */}
        <div className="bg-white rounded-lg shadow p-6">
          {renderCalendarHeader()}
          {renderCalendarDays()}
        </div>

        {/* Booking Details Section */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-xl font-bold mb-4">Booking Details</h3>
          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Selected Date Display */}
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">
                Selected Date
              </label>
              <div className="p-2 bg-gray-100 rounded">
                {selectedDate ? format(selectedDate, 'EEEE, MMMM do, yyyy') : 'Please select a date'}
              </div>
            </div>

            {/* Time Selection */}
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">
                Select Time
              </label>
              <select
                value={bookingDetails.time}
                onChange={(e) => setBookingDetails(prev => ({ ...prev, time: e.target.value }))}
                className="w-full p-2 border rounded"
                required
              >
                <option value="">Choose a time</option>
                {timeSlots.map(time => (
                  <option key={time} value={time}>{time}</option>
                ))}
              </select>
            </div>

            {/* Phone Number */}
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">
                Phone Number
              </label>
              <input
                type="tel"
                value={bookingDetails.phoneNumber}
                onChange={(e) => setBookingDetails(prev => ({ ...prev, phoneNumber: e.target.value }))}
                placeholder="123-456-7890"
                className="w-full p-2 border rounded"
                required
              />
            </div>

            {/* Event Type */}
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">
                Event Type
              </label>
              <select
                value={bookingDetails.eventType}
                onChange={(e) => setBookingDetails(prev => ({ ...prev, eventType: e.target.value }))}
                className="w-full p-2 border rounded"
                required
              >
                <option value="">Select event type</option>
                {eventTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            </div>

            {/* Games Selection */}
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">
                Select Games
              </label>
              <div className="grid grid-cols-2 gap-2">
                {gameOptions.map(game => (
                  <label key={game.id} className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      value={game.name}
                      checked={bookingDetails.selectedGames.includes(game.name)}
                      onChange={(e) => {
                        const games = e.target.checked
                          ? [...bookingDetails.selectedGames, game.name]
                          : bookingDetails.selectedGames.filter(g => g !== game.name);
                        setBookingDetails(prev => ({ ...prev, selectedGames: games }));
                      }}
                      className="form-checkbox"
                    />
                    <span>{game.name}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Tables and Guests */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 font-bold mb-2">
                  Number of Tables
                </label>
                <input
                  type="number"
                  value={bookingDetails.numberOfTables}
                  onChange={(e) => setBookingDetails(prev => ({ ...prev, numberOfTables: e.target.value }))}
                  min="1"
                  className="w-full p-2 border rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 font-bold mb-2">
                  Guest Count
                </label>
                <input
                  type="number"
                  value={bookingDetails.guestCount}
                  onChange={(e) => setBookingDetails(prev => ({ ...prev, guestCount: e.target.value }))}
                  min="1"
                  className="w-full p-2 border rounded"
                  required
                />
              </div>
            </div>

            {/* Special Requests */}
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">
                Special Requests
              </label>
              <textarea
                value={bookingDetails.specialRequests}
                onChange={(e) => setBookingDetails(prev => ({ ...prev, specialRequests: e.target.value }))}
                className="w-full p-2 border rounded"
                rows="4"
              />
            </div>

            {user ? (
              <button
                type="submit"
                disabled={loading}
                className={`w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition-colors
                  ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
              >
                {loading ? 'Processing...' : 'Book Event'}
              </button>
            ) : (
              <div className="text-center space-y-4">
                <p className="text-gray-700">Please log in or sign up to complete your booking</p>
                <div className="space-x-4">
                  <Link
                    to="/login"
                    className="inline-block bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
                  >
                    Login
                  </Link>
                  <Link
                    to="/register"
                    className="inline-block bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600"
                  >
                    Sign Up
                  </Link>
                </div>
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default InteractiveBookingCalendar;
