import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { 
  Card, 
  CardHeader, 
  CardTitle, 
  CardContent,
  Button,
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
  Tabs,
  TabsList,
  TabsTrigger,
  TabsContent
} from './ui/shared';

const formatDate = (date) => {
  return new Date(date).toLocaleDateString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });
};

const Dashboard = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showCancelDialog, setShowCancelDialog] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      const response = await api.get('/bookings/my');
      setBookings(response.data);
    } catch (err) {
      setError('Failed to fetch your bookings');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelBooking = async () => {
    try {
      await api.patch(`/bookings/${selectedBooking._id}/cancel`);
      setShowCancelDialog(false);
      fetchBookings();
    } catch (err) {
      setError('Failed to cancel booking');
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-screen text-red-500">
        {error}
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Welcome, {user?.firstName || 'User'}!</h1>
        <Button>
          <Link to="/booking" className="text-white">Book New Event</Link>
        </Button>
      </div>

      <Tabs>
        <TabsList>
          <TabsTrigger>Upcoming Events</TabsTrigger>
          <TabsTrigger>Past Events</TabsTrigger>
          <TabsTrigger>Cancelled Events</TabsTrigger>
        </TabsList>

        <TabsContent>
          <Card>
            <CardHeader>
              <CardTitle>Upcoming Events</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {bookings
                  .filter(booking => 
                    new Date(booking.date) > new Date() && 
                    booking.status !== 'cancelled'
                  )
                  .map(booking => (
                    <BookingCard
                      key={booking._id}
                      booking={booking}
                      onCancel={() => {
                        setSelectedBooking(booking);
                        setShowCancelDialog(true);
                      }}
                    />
                  ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent>
          <Card>
            <CardHeader>
              <CardTitle>Past Events</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {bookings
                  .filter(booking => 
                    new Date(booking.date) < new Date() && 
                    booking.status !== 'cancelled'
                  )
                  .map(booking => (
                    <BookingCard
                      key={booking._id}
                      booking={booking}
                      isPast
                    />
                  ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent>
          <Card>
            <CardHeader>
              <CardTitle>Cancelled Events</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {bookings
                  .filter(booking => booking.status === 'cancelled')
                  .map(booking => (
                    <BookingCard
                      key={booking._id}
                      booking={booking}
                      isCancelled
                    />
                  ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      <Dialog open={showCancelDialog} onOpenChange={setShowCancelDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Cancel Booking</DialogTitle>
          </DialogHeader>
          <p>Are you sure you want to cancel this booking?</p>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowCancelDialog(false)}>
              No, Keep It
            </Button>
            <Button variant="destructive" onClick={handleCancelBooking}>
              Yes, Cancel
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

const BookingCard = ({ booking, onCancel, isPast, isCancelled }) => {
  return (
    <Card>
      <CardContent className="p-4">
        <div className="flex justify-between items-start">
          <div>
            <h3 className="font-medium">
              {booking.eventType} - {formatDate(booking.date)}
            </h3>
            <p className="text-sm text-gray-600">
              Status: {booking.status}
            </p>
            <p className="text-sm">
              Tables: {booking.numberOfTables}
            </p>
            <p className="text-sm">
              Games: {booking.selectedGames.join(', ')}
            </p>
          </div>
          {!isPast && !isCancelled && booking.status !== 'cancelled' && (
            <Button
              variant="destructive"
              onClick={onCancel}
            >
              Cancel Booking
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default Dashboar
